mapper.select:
select  
from t_user where id in( select user_id from t_user_role where role_id=#{roleId,jdbcType=VARCHAR}) and  code_data like  CONCAT(#{codeData,jdbcType=VARCHAR},'%')

	and user_name like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')

mapper.update:
update t_user
set user_name = #{userName,jdbcType=VARCHAR},
password = #{password,jdbcType=VARCHAR},
status = #{status,jdbcType=CHAR},
code_data = #{codeData,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}
